import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import friends from "./friends.json";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    clicked:[],
    score: 0,
    highScore : 0
  };

  click = id => {
    const shuffledArray = this.shuffleArray(friends);
    this.setState({friends: shuffledArray});
    console.log("clicked")
  }

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <div>
      <nav className="navbar navbar-light" styleName="background-color: #e3f2fd;">
      <h1>Click on the image Only Once! </h1>  </nav>
      <Wrapper>
        {this.state.friends.map(friend => (  //dont understand this part
          <FriendCard
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
          />
        ))}
      </Wrapper>
      </div>
    );
  }
}

export default App;
