import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
//import Title from "./components/Title";
import friends from "./friends.json";

var bgColors ={
  "Default": "#e3f2fd",
  "main" : "#ECEFF1"
}

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    clicked:[],
    score: 0,
    highScore : 0
  };

  shuffle = (array) => {
    //console.log(array.length)
    for (var i = array.length-1; i > 0  ; i--){
      const j = Math.floor(Math.random()* (i+1));
      [array[i],array[j]]= [array[j],array[i]]
    }
   // console.log(array);
    return array
  }



  clicked = id => {
    const shuffledArray = this.shuffle(friends);
    //console.log({friends :shuffledArray})
    this.setState({friends :shuffledArray});
    //console.log ( this.state.clicked.length)
     this.state.clicked.push(id);
    // for (var i = 0; i < this.state.clicked.length; i ++ ){
    //   console.log ("inside loop")
    //       if(id === this.state.clicked[i]){
    //           console.log("game over")
    //           break;
    //       } else {
    //       //  this.state.clicked.push(id);
    //         console.log("new added "+this.state.clicked);
    //         this.setState({
    //           score : this.state.score+ 1
    //         }) 
    //       }
    // }

     for (var i = this.state.clicked.length; i > 0; i -- ){
       console.log(id)
        for (var j = 0; j< i; j ++ ){
          if(id === this.state.clicked[i]){

             console.log("game over" + this.state.clicked[i])
             break;
        }else {
          this.setState({
                      score : this.state.score+ 1
                    }) 
        }
      }
     }


    //   if (this.state.clicked.includes(id)) {
  //     this.setState({ score: 0, clicked: [], message: "Game Over. Click an image to start again!", });
  //   }
  // //   else {
  // //     this.setState({
  // //       clicked: this.state.clicked.concat([id]),
  // //       score: this.state.score + 1,
  // //       message: "Correct",

  // //     });
  // // }
}




  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
    <div style={{backgroundColor: bgColors.main}} >
      <nav  className="navbar navbar-light row" style={{backgroundColor: bgColors.Default}}>
          <div className="col-3"></div> 
            <div className="col-6">
            <h1>Click on the image Only Once! </h1>  
            </div>
            <div className="col-3">
            <div className="row">Score : {this.state.score}</div>
            <div className="row">highScore : {this.state.highScore}</div>
            </div>

      </nav>


      <Wrapper>
        {this.state.friends.map(friend => (  //dont understand this part
          <FriendCard
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            clicked ={this.clicked}

          />
        ))}
      </Wrapper>
      <footer> 
      <nav className="navbar navbar-light" style={{backgroundColor: bgColors.Default}}>
      <h3>This is footer</h3> </nav>
      </footer>
    </div>
    );
  }
}

export default App;
